using System;\nusing System.DirectoryServices;\n\npublic class UserManagement\n{\n    public static void CreateUser(string ldapPath, string username, string password)\n    {\n        try\n        {\n            DirectoryEntry dirEntry = new DirectoryEntry(ldapPath);\n            DirectoryEntry newUser = dirEntry.Children.Add("CN=" + username, "user");\n\n            newUser.Properties["samAccountName"].Value = username;\n            newUser.CommitChanges();\n\n            // Set the user's password\n            newUser.Invoke("SetPassword", new object[] { password });\n            newUser.Properties["userAccountControl"].Value = 0x200; // Enable account\n            newUser.CommitChanges();\n\n            Console.WriteLine("User created successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n\n    public static void DeleteUser(string ldapPath, string username)\n    {\n        try\n        {\n            DirectoryEntry dirEntry = new DirectoryEntry(ldapPath);\n            DirectoryEntry user = dirEntry.Children.Find("CN=" + username);\n            dirEntry.Children.Remove(user);\n\n            Console.WriteLine("User deleted successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n\n    public static void Main()\n    {\n        string ldapPath = "LDAP://CN=Users,DC=yourdomain,DC=com";\n        string username = "newuser";\n        string password = "P@ssw0rd";\n\n        CreateUser(ldapPath, username, password);\n        DeleteUser(ldapPath, username);\n    }\n}