// Define the service contract\n[ServiceContract]\npublic interface IComService\n{\n    [OperationContract]\n    void YourMethod();\n}\n\n// Implement the service contract\npublic class ComService : IComService\n{\n    public void YourMethod()\n    {\n        // Call the 32-bit COM server method\n        var comWrapper = new ComWrapper();\n        comWrapper.YourMethod();\n    }\n}\n\n// Host the WCF service in your 32-bit COM server application\nServiceHost host = new ServiceHost(typeof(ComService));\nhost.Open();