DataTable dataTable1 = new DataTable();\n\n// Assuming you have a method to get an IDataReader\nusing (IDataReader dataReader = GetDataReader())\n{\n    dataTable1.Load(dataReader);\n}\n\n// To append data from another reader, you can load it into a new DataTable first\nDataTable dataTable2 = new DataTable();\nusing (IDataReader dataReader2 = GetDataReader())\n{\n    dataTable2.Load(dataReader2);\n}\n\n// Then merge the second DataTable into the first\ndataTable1.Merge(dataTable2);