using System;\nusing System.Configuration;\nusing System.Configuration.Internal;\nusing System.Web.Configuration;\n\nclass EncryptConfigSection\n{\n    static void Main(string[] args)\n    {\n        if (args.Length < 2)\n        {\n            Console.WriteLine("Usage: EncryptConfigSection <sectionName> <configFilePath>");\n            return;\n        }\n\n        string sectionName = args[0];\n        string configFilePath = args[1];\n\n        Configuration config = WebConfigurationManager.OpenWebConfiguration(configFilePath);\n        ConfigurationSection section = config.GetSection(sectionName);\n\n        if (section != null && !section.SectionInformation.IsProtected)\n        {\n            section.SectionInformation.ProtectSection("DataProtectionConfigurationProvider");\n            section.SectionInformation.ForceSave = true;\n            config.Save(ConfigurationSaveMode.Full);\n            Console.WriteLine($"Section '{sectionName}' has been successfully encrypted.");\n        }\n        else\n        {\n            Console.WriteLine($"Section '{sectionName}' is either not found or already encrypted.");\n        }\n    }\n}