using System;\nusing System.Net;\nusing System.Xml;\n\nclass WebDAVExample\n{\n    static void Main()\n    {\n        string exchangeUrl = "http://your-exchange-server/exchange/username/calendar/";\n        string userName = "your-username";\n        string password = "your-password";\n        \n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(exchangeUrl);\n        request.Credentials = new NetworkCredential(userName, password);\n        request.Method = "SEARCH";\n        request.ContentType = "text/xml";\n        \n        string searchRequest = \n            "<?xml version=\"1.0\"?>" +\n            "<D:searchrequest xmlns:D=\"DAV:\">" +\n            "<D:sql>SELECT \"DAV:displayname\" FROM scope('SHALLOW TRAVERSAL OF \"" + exchangeUrl + "\"')</D:sql>" +\n            "</D:searchrequest>";\n        \n        byte[] bytes = System.Text.Encoding.UTF8.GetBytes(searchRequest);\n        request.ContentLength = bytes.Length;\n        \n        using (var requestStream = request.GetRequestStream())\n        {\n            requestStream.Write(bytes, 0, bytes.Length);\n        }\n        \n        try\n        {\n            HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n            using (var responseStream = response.GetResponseStream())\n            {\n                XmlDocument doc = new XmlDocument();\n                doc.Load(responseStream);\n                Console.WriteLine(doc.InnerXml);\n            }\n        }\n        catch (WebException webEx)\n        {\n            Console.WriteLine("Error: " + webEx.Message);\n        }\n    }\n}