using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string remoteMachineName = "RemoteMachineName";\n        string processName = "notepad.exe"; // Example process to kill\n        string username = "YourUsername";\n        string password = "YourPassword";\n\n        ConnectionOptions options = new ConnectionOptions();\n        options.Username = username;\n        options.Password = password;\n        options.Impersonation = ImpersonationLevel.Impersonate;\n\n        ManagementScope scope = new ManagementScope($@"\\{remoteMachineName}\root\cimv2", options);\n        \n        try\n        {\n            scope.Connect();\n            ObjectQuery query = new ObjectQuery($"SELECT * FROM Win32_Process WHERE Name = '{processName}'");\n            ManagementObjectSearcher searcher = new ManagementObjectSearcher(scope, query);\n            ManagementObjectCollection processList = searcher.Get();\n\n            foreach (ManagementObject obj in processList)\n            {\n                obj.InvokeMethod("Terminate", null);\n                Console.WriteLine($"Process {processName} terminated successfully on {remoteMachineName}");\n            }\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine($"An error occurred: {e.Message}");\n        }\n    }\n}