private void ValidateBindings(DependencyObject element)\n{\n    // Get the BindingExpression for the SelectedItem property\n    var selectedItemBindingExpression = BindingOperations.GetBindingExpression(element, ComboBox.SelectedItemProperty);\n    \n    if (selectedItemBindingExpression != null)\n    {\n        var binding = selectedItemBindingExpression.ParentBinding;\n        \n        // Validate the validation rules of the binding\n        foreach (ValidationRule rule in binding.ValidationRules)\n        {\n            ValidationResult result = rule.Validate(element.GetValue(ComboBox.SelectedItemProperty), CultureInfo.CurrentCulture);\n\n            selectedItemBindingExpression.UpdateSource();\n\n            if (!result.IsValid)\n            {\n                ErrorMessages.Add(result.ErrorContent.ToString());\n            }\n\n            IsContentValid &= result.IsValid;\n        }\n    }\n    \n    // Iterate over all other dependency properties\n    Type elementType = element.GetType();\n    FieldInfo[] dependencyPropertyFields = elementType.GetFields(BindingFlags.Static | BindingFlags.Public | BindingFlags.DeclaredOnly);\n\n    foreach (FieldInfo dependencyPropertyField in dependencyPropertyFields)\n    {\n        DependencyProperty dependencyProperty = dependencyPropertyField.GetValue(element) as DependencyProperty;\n\n        if (dependencyProperty != null)\n        {\n            Binding binding = BindingOperations.GetBinding(element, dependencyProperty);\n            BindingExpression bindingExpression = BindingOperations.GetBindingExpression(element, dependencyProperty);\n\n            if (binding != null && bindingExpression != null)\n            {\n                foreach (ValidationRule rule in binding.ValidationRules)\n                {\n                    ValidationResult result = rule.Validate(element.GetValue(dependencyProperty), CultureInfo.CurrentCulture);\n                    \n                    bindingExpression.UpdateSource();\n\n                    if (!result.IsValid)\n                    {\n                        ErrorMessages.Add(result.ErrorContent.ToString());\n                    }\n\n                    IsContentValid &= result.IsValid;\n                }\n            }\n        }\n    }\n}