using System;\nusing Saxon.Api;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Processor processor = new Processor();\n        DocumentBuilder builder = processor.NewDocumentBuilder();\n        builder.BaseUri = new Uri("file:///");\n\n        // Load the XML document\n        XdmNode input = builder.Build(new Uri("file:///path/to/your/xmlfile.xml"));\n\n        // Create an XPath compiler\n        XPathCompiler compiler = processor.NewXPathCompiler();\n        compiler.DeclareNamespace("fn", "http://www.w3.org/2005/xpath-functions");\n\n        // Compile the XPath expression\n        XPathSelector selector = compiler.Compile("fn:ends-with(/myXml/data, 'World')").Load();\n\n        // Set the context node\n        selector.ContextItem = input;\n\n        // Evaluate the expression\n        XdmAtomicValue result = (XdmAtomicValue)selector.EvaluateSingle();\n\n        // Output the result\n        Console.WriteLine(result.Value);\n    }\n}