using System;\nusing System.Data.SqlClient;\n\npublic class MediaDataRepository\n{\n    private string connectionString = "your_connection_string_here";\n\n    public void InsertMediaData(byte[] imageData, byte[] audioData, byte[] textData)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            string query = "INSERT INTO MediaData (ImageData, AudioData, TextData) VALUES (@ImageData, @AudioData, @TextData)";\n            SqlCommand command = new SqlCommand(query, connection);\n            command.Parameters.AddWithValue("@ImageData", imageData);\n            command.Parameters.AddWithValue("@AudioData", audioData);\n            command.Parameters.AddWithValue("@TextData", textData);\n            \n            connection.Open();\n            command.ExecuteNonQuery();\n        }\n    }\n    \n    public byte[] GetImageData(int id)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            string query = "SELECT ImageData FROM MediaData WHERE Id = @Id";\n            SqlCommand command = new SqlCommand(query, connection);\n            command.Parameters.AddWithValue("@Id", id);\n            \n            connection.Open();\n            return command.ExecuteScalar() as byte[];\n        }\n    }\n    \n    // Additional methods for update and delete can be implemented similarly.\n}