using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("coredll.dll", EntryPoint = "RegSetValueExW")]\n    public static extern int RegSetValueExW(uint hKey, string lpValueName,\n                uint lpReserved,\n                uint lpType,\n                byte[] lpData,\n                uint lpcbData);\n\n    public static void SetRegistryDword(uint hKey, string lpValueName, int value)\n    {\n        // Convert the integer value to a byte array\n        byte[] data = BitConverter.GetBytes(value);\n        \n        // REG_DWORD is typically represented by 4 (0x4)\n        uint REG_DWORD = 4;\n        \n        // Call the P/Invoke function\n        int result = RegSetValueExW(hKey, lpValueName, 0, REG_DWORD, data, (uint)data.Length);\n        \n        if (result != 0)\n        {\n            // Handle error (result contains the error code)\n            Console.WriteLine("Error setting registry value: " + result);\n        }\n    }\n\n    static void Main()\n    {\n        uint hKey = 0x80000002; // Example: HKEY_LOCAL_MACHINE\n        string valueName = "MyValue";\n        int value = 5;\n\n        SetRegistryDword(hKey, valueName, value);\n    }\n}