public class MyWebPart : WebPart\n   {\n       protected override void Render(HtmlTextWriter writer)\n       {\n           base.Render(writer);\n\n           try\n           {\n               ComInteropWrapper wrapper = new ComInteropWrapper();\n               wrapper.SafeMethod();\n               writer.Write("Call to unmanaged code was successful.");\n           }\n           catch (Exception ex)\n           {\n               writer.Write($"Error: {ex.Message}");\n           }\n       }\n   }