using System.IO;\nusing Ionic.Zip;\n\nstring tempPath = Path.Combine(Path.GetTempPath(), "temp.zip");\n\n// Step 1: Save the zip file to a temporary location on the server\nusing (ZipFile zip = new ZipFile())\n{\n    // Add files to the zip archive here\n    zip.AddFile("file1.txt", "");\n    zip.AddFile("file2.txt", "");\n\n    zip.Save(tempPath);\n}\n\n// Step 2: Set appropriate permissions (if needed)\n// Note: This step may vary based on your server environment and specific requirements\n\n// Step 3: Stream the file to the client\nResponse.ContentType = "application/zip";\nResponse.AddHeader("content-disposition", "attachment; filename=" + filename);\n\nbyte[] fileData = File.ReadAllBytes(tempPath);\nResponse.OutputStream.Write(fileData, 0, fileData.Length);\nResponse.Flush();\nResponse.End();\n\n// Clean up the temporary file\nFile.Delete(tempPath);