var nextExample = (from e in context.Examples\n                   where e.TopicID == (from ex in context.Examples\n                                       where ex.ExampleID == exampleId\n                                       select ex.TopicID).FirstOrDefault()\n                         && e.ExampleOrder > (from ex in context.Examples\n                                              where ex.ExampleID == exampleId\n                                              select ex.ExampleOrder).FirstOrDefault()\n                   orderby e.ExampleOrder\n                   select e)\n                  .FirstOrDefault();