using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class MultiColorLabel : Form\n{\n    private RichTextBox richTextBox;\n\n    public MultiColorLabel()\n    {\n        this.Text = "Multi-Color Label Example";\n        this.Size = new Size(400, 200);\n\n        richTextBox = new RichTextBox();\n        richTextBox.Dock = DockStyle.Fill;\n        richTextBox.ReadOnly = true;\n        richTextBox.BorderStyle = BorderStyle.None;\n        richTextBox.BackColor = this.BackColor;\n        richTextBox.TabStop = false;\n\n        AddColoredText("Red, ", Color.Red);\n        AddColoredText("Green, ", Color.Green);\n        AddColoredText("Blue", Color.Blue);\n\n        this.Controls.Add(richTextBox);\n    }\n\n    private void AddColoredText(string text, Color color)\n    {\n        richTextBox.SelectionStart = richTextBox.TextLength;\n        richTextBox.SelectionLength = 0;\n        richTextBox.SelectionColor = color;\n        richTextBox.AppendText(text);\n        richTextBox.SelectionColor = richTextBox.ForeColor;\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MultiColorLabel());\n    }\n}