using System;\nusing System.IO;\nusing DinkToPdf;\nusing DinkToPdf.Contracts;\n\nclass Program\n{\n    static void Main()\n    {\n        var converter = new SynchronizedConverter(new PdfTools());\n\n        var htmlContent = @"<html><body><h1>Hello World</h1><p>This is a sample HTML to image conversion.</p></body></html>";\n\n        var doc = new HtmlToImageDocument\n        {\n            Objects = { new ObjectSettings { HtmlContent = htmlContent } },\n            Format = "png"\n        };\n\n        byte[] image = converter.Convert(doc);\n\n        File.WriteAllBytes("output.png", image);\n\n        Console.WriteLine("HTML has been converted to image successfully.");\n    }\n}\n\npublic class HtmlToImageDocument : HtmlToPdfDocument\n{\n    public string Format { get; set; }\n\n    public HtmlToImageDocument()\n    {\n        this.GlobalSettings = new GlobalSettings\n        {\n            OutputFormat = this.Format,\n        };\n    }\n}