using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string driveLetter = @"Z:\"; // Replace with the drive letter of your mount\n\n        if (IsDriveAvailable(driveLetter))\n        {\n            Console.WriteLine("Drive is available.");\n        }\n        else\n        {\n            Console.WriteLine("Drive is not available.");\n        }\n    }\n\n    static bool IsDriveAvailable(string driveLetter)\n    {\n        try\n        {\n            DriveInfo driveInfo = new DriveInfo(driveLetter);\n            return driveInfo.IsReady;\n        }\n        catch (Exception)\n        {\n            return false;\n        }\n    }\n}