using Sitecore.Security.Accounts;\nusing Sitecore.Security.Domains;\n\npublic void CreateRoleHierarchy(string parentRoleName, string childRoleName)\n{\n    // Ensure the roles exist\n    if (!Role.Exists(parentRoleName))\n    {\n        Role parentRole = Role.Create(Domain.GetDefaultDomain().Name + "\\" + parentRoleName);\n    }\n\n    if (!Role.Exists(childRoleName))\n    {\n        Role childRole = Role.Create(Domain.GetDefaultDomain().Name + "\\" + childRoleName);\n    }\n\n    // Add child role to parent role\n    User parentRoleUser = User.FromName(parentRoleName, true);\n    parentRoleUser.Roles.Add(Role.FromName(childRoleName));\n}\n\n// Example usage\nCreateRoleHierarchy("sitecore\\ParentRole", "sitecore\\ChildRole");