using System.Collections.Generic;\nusing System.Globalization;\nusing System.Windows.Controls;\n\npublic class UniqueNameValidationRule : ValidationRule\n{\n    public IList<string> ExistingNames { get; set; }\n\n    public override ValidationResult Validate(object value, CultureInfo cultureInfo)\n    {\n        if (value == null || ExistingNames == null)\n        {\n            return ValidationResult.ValidResult;\n        }\n\n        string nameToValidate = value.ToString();\n\n        if (ExistingNames.Contains(nameToValidate))\n        {\n            return new ValidationResult(false, "Name must be unique.");\n        }\n\n        return ValidationResult.ValidResult;\n    }\n}