using System;\nusing System.IO;\nusing System.Net;\nusing System.Net.NetworkInformation;\n\npublic class PathChecker\n{\n    public static bool IsLocal(string path)\n    {\n        if (string.IsNullOrWhiteSpace(path))\n            throw new ArgumentException("Path cannot be null or empty.", nameof(path));\n\n        Uri uri;\n        try\n        {\n            uri = new Uri(path);\n        }\n        catch (UriFormatException)\n        {\n            // If path is not a valid URI, assume it's a local path\n            return true;\n        }\n\n        if (!uri.IsUnc)\n        {\n            // If it's not a UNC path, it's a local path\n            return true;\n        }\n\n        string hostName = uri.Host;\n        if (IsLocalHost(hostName))\n        {\n            return true;\n        }\n\n        return false;\n    }\n\n    private static bool IsLocalHost(string hostName)\n    {\n        if (string.Equals(hostName, Dns.GetHostName(), StringComparison.OrdinalIgnoreCase))\n        {\n            return true;\n        }\n\n        foreach (IPAddress localIP in Dns.GetHostAddresses(Dns.GetHostName()))\n        {\n            if (IPAddress.TryParse(hostName, out IPAddress hostIP))\n            {\n                if (hostIP.Equals(localIP))\n                {\n                    return true;\n                }\n            }\n        }\n\n        return false;\n    }\n}