using System;\nusing System.Collections.Generic;\nusing TimeZoneConverter; // You may need a library like TimeZoneConverter\n\npublic class User\n{\n    public string TimeZoneId { get; set; }\n}\n\npublic class DateTimeHelper\n{\n    public static DateTime GetUserLocalTime(DateTime utcDateTime, string timeZoneId)\n    {\n        TimeZoneInfo userTimeZone = TZConvert.GetTimeZoneInfo(timeZoneId);\n        DateTime userLocalTime = TimeZoneInfo.ConvertTimeFromUtc(utcDateTime, userTimeZone);\n        return userLocalTime;\n    }\n}\n\n// Example usage\npublic class Program\n{\n    public static void Main()\n    {\n        User user = new User { TimeZoneId = "America/New_York" };\n        DateTime utcDateTime = DateTime.UtcNow; // Example UTC DateTime\n\n        DateTime localDateTime = DateTimeHelper.GetUserLocalTime(utcDateTime, user.TimeZoneId);\n        Console.WriteLine($"UTC Time: {utcDateTime}, Local Time: {localDateTime}");\n    }\n}