using Microsoft.VisualStudio.Modeling;\n\npublic void PerformHiddenTransaction(Store store, Action action)\n{\n    using (Transaction transaction = store.TransactionManager.BeginTransaction("Hidden Transaction", false))\n    {\n        try\n        {\n            // Suppress undo recording\n            store.UndoManager.UndoState = UndoState.Disabled;\n\n            // Perform your action\n            action.Invoke();\n\n            // Commit the transaction\n            transaction.Commit();\n        }\n        finally\n        {\n            // Always re-enable the undo manager to ensure other operations are not affected\n            store.UndoManager.UndoState = UndoState.Enabled;\n        }\n    }\n}