using System;\nusing System.DirectoryServices.AccountManagement;\n\npublic class DomainCredentialValidator\n{\n    public bool ValidateCredentials(string domain, string username, string password)\n    {\n        // Set the context type to Domain\n        using (PrincipalContext context = new PrincipalContext(ContextType.Domain, domain))\n        {\n            // Validate the credentials\n            return context.ValidateCredentials(username, password);\n        }\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string domain = "STACKOVERFLOW";\n        string username = "joel";\n        string password = "splotchy";\n\n        DomainCredentialValidator validator = new DomainCredentialValidator();\n        bool isValid = validator.ValidateCredentials(domain, username, password);\n\n        if (isValid)\n        {\n            Console.WriteLine("Credentials are valid.");\n        }\n        else\n        {\n            Console.WriteLine("Invalid credentials.");\n        }\n    }\n}