[WebMethod]\n    public string RenewAuthTicket(string username)\n    {\n        // Validate the user and create a new forms authentication ticket\n        if (ValidateUser(username))\n        {\n            FormsAuthenticationTicket ticket = new FormsAuthenticationTicket(\n                1,                                   // Ticket version\n                username,                            // Username\n                DateTime.Now,                        // Issue date\n                DateTime.Now.AddMinutes(20),         // Expiration date\n                false,                               // Persistent\n                userData,                            // User data\n                FormsAuthentication.FormsCookiePath  // Cookie path\n            );\n\n            // Encrypt the ticket\n            string encryptedTicket = FormsAuthentication.Encrypt(ticket);\n            return encryptedTicket;\n        }\n        return null;\n    }