using System;\nusing System.IO;\nusing System.Net;\nusing System.Web.Services;\n\n[WebService(Namespace = "http://yournamespace.org/")]\n[WebServiceBinding(ConformsTo = WsiProfiles.BasicProfile1_1)]\npublic class WeatherService : System.Web.Services.WebService\n{\n    [WebMethod]\n    public string GetWeatherData(string queryString)\n    {\n        string url = String.Format("http://[apicode].api.wxbug.net/weatherservice.asmx?{0}", queryString);\n        WebRequest request = WebRequest.Create(url);\n        WebResponse response = request.GetResponse();\n\n        using (StreamReader sr = new StreamReader(response.GetResponseStream()))\n        {\n            return sr.ReadToEnd();\n        }\n    }\n}