using System;\nusing System.Data.SqlClient;\nusing System.IO;\n\npublic void InsertCertification(int personID, string certName, DateTime certDate, string pdfFilePath)\n{\n    byte[] pdfData = File.ReadAllBytes(pdfFilePath);\n\n    using (SqlConnection connection = new SqlConnection("YourConnectionString"))\n    {\n        connection.Open();\n        using (SqlCommand command = new SqlCommand("INSERT INTO Certifications (PersonID, CertificationName, CertificationDate, CertificationPDF) VALUES (@PersonID, @CertName, @CertDate, @CertPDF)", connection))\n        {\n            command.Parameters.AddWithValue("@PersonID", personID);\n            command.Parameters.AddWithValue("@CertName", certName);\n            command.Parameters.AddWithValue("@CertDate", certDate);\n            command.Parameters.AddWithValue("@CertPDF", pdfData);\n\n            command.ExecuteNonQuery();\n        }\n    }\n}