using Outlook = Microsoft.Office.Interop.Outlook;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Outlook.Application oApp = new Outlook.Application();\n        Outlook.NameSpace oNS = oApp.GetNamespace("mapi");\n        Outlook.Recipient oRecip = oNS.CreateRecipient("AccountNameHere");\n        oRecip.Resolve();\n        \n        if (oRecip.Resolved)\n        {\n            Outlook.MAPIFolder rootFolder = oNS.GetSharedDefaultFolder(oRecip, Outlook.OlDefaultFolders.olFolderInbox).Parent;\n            Outlook.Folders topFolders = rootFolder.Folders;\n\n            foreach (Outlook.MAPIFolder folder in topFolders)\n            {\n                Console.WriteLine("Folder: " + folder.Name);\n                // You can use the folder name to get the specific folder\n                if (folder.Name == "YourFolderName")\n                {\n                    Outlook.Items folderItems = folder.Items;\n                    int itemCount = folderItems.Count;\n                    Console.WriteLine($"There are {itemCount} items in the folder {folder.Name}.");\n                }\n            }\n        }\n    }\n}