using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Program\n{\n    public static void Main()\n    {\n        IList<int> items = GetItemsFromSomeWhere();\n        var sortedItems = items.OrderBy(x => x).ToList();\n        \n        // Print sorted items\n        sortedItems.ForEach(Console.WriteLine);\n\n        int item = GetItemSomwHow();\n        int index = items.IndexOf(item);\n        Console.WriteLine("Index of item: " + index);\n    }\n\n    private static IList<int> GetItemsFromSomeWhere()\n    {\n        return new List<int> { 5, 3, 8, 1, 9 };\n    }\n\n    private static int GetItemSomwHow()\n    {\n        return 8;\n    }\n}