using System;\nusing System.Web.UI.WebControls;\nusing System.Web.UI;\n\npublic partial class YourModule : PortalModuleBase\n{\n    protected Repeater rptExample;\n\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            LoadTemplates();\n            BindData();\n        }\n    }\n\n    private void LoadTemplates()\n    {\n        // Assuming the templates are stored in a database or file system\n        string itemTemplate = GetTemplateFromDatabase("ItemTemplate");\n        string headerTemplate = GetTemplateFromDatabase("HeaderTemplate");\n        string footerTemplate = GetTemplateFromDatabase("FooterTemplate");\n\n        rptExample.ItemTemplate = CreateTemplate(itemTemplate);\n        rptExample.HeaderTemplate = CreateTemplate(headerTemplate);\n        rptExample.FooterTemplate = CreateTemplate(footerTemplate);\n    }\n\n    private ITemplate CreateTemplate(string templateContent)\n    {\n        return new CompiledTemplateBuilder(delegate(Control container)\n        {\n            LiteralControl lc = new LiteralControl(templateContent);\n            container.Controls.Add(lc);\n        });\n    }\n\n    private string GetTemplateFromDatabase(string templateName)\n    {\n        // Replace this with actual code to retrieve the template from the database\n        // For example purposes, returning hardcoded template strings\n        if (templateName == "ItemTemplate")\n        {\n            return "<div><%# Eval('YourDataField') %></div>";\n        }\n        else if (templateName == "HeaderTemplate")\n        {\n            return "<div>Header</div>";\n        }\n        else if (templateName == "FooterTemplate")\n        {\n            return "<div>Footer</div>";\n        }\n\n        return string.Empty;\n    }\n\n    private void BindData()\n    {\n        // Replace with your actual data source\n        var dataSource = new List<YourDataType>\n        {\n            new YourDataType { YourDataField = "Example1" },\n            new YourDataType { YourDataField = "Example2" }\n        };\n\n        rptExample.DataSource = dataSource;\n        rptExample.DataBind();\n    }\n\n    public class YourDataType\n    {\n        public string YourDataField { get; set; }\n    }\n}