using System;\nusing System.Collections.Generic;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string startDirectory = @"C:\"; // or any other starting directory like "K:\"\n        List<string> files = new List<string>();\n        \n        try\n        {\n            GetFilesRecursively(startDirectory, files);\n        }\n        catch (UnauthorizedAccessException ex)\n        {\n            Console.WriteLine($"Access denied to path: {ex.Message}");\n        }\n\n        foreach (var file in files)\n        {\n            Console.WriteLine(file);\n        }\n    }\n\n    static void GetFilesRecursively(string directory, List<string> files)\n    {\n        try\n        {\n            foreach (string file in Directory.GetFiles(directory))\n            {\n                files.Add(file);\n            }\n\n            foreach (string subDirectory in Directory.GetDirectories(directory))\n            {\n                GetFilesRecursively(subDirectory, files);\n            }\n        }\n        catch (UnauthorizedAccessException ex)\n        {\n            // Handle the access denied exception\n            Console.WriteLine($"Access denied to path: {directory} - {ex.Message}");\n        }\n        catch (Exception ex)\n        {\n            // Handle other possible exceptions\n            Console.WriteLine($"Error accessing path: {directory} - {ex.Message}");\n        }\n    }\n}