using System;\nusing System.Runtime.InteropServices;\nusing System.Windows.Forms;\n\npublic class KeyInterceptor\n{\n    private const int SHCMBM_OVERRIDEKEY = 0x00000160 + 403;\n    private const int VK_TTALK = 0xF3; // Virtual key code for the Talk key\n    private const int WM_HOTKEY = 0x0312;\n\n    [DllImport("aygshell.dll")]\n    private static extern int SHCMBMOverrideKey(IntPtr hwnd, int uMsg, int wParam, int lParam);\n\n    private IntPtr _windowHandle;\n\n    public KeyInterceptor(IntPtr windowHandle)\n    {\n        _windowHandle = windowHandle;\n        OverrideTalkKey();\n    }\n\n    private void OverrideTalkKey()\n    {\n        SHCMBMOverrideKey(_windowHandle, SHCMBM_OVERRIDEKEY, 0, VK_TTALK);\n    }\n\n    public void ProcessMessage(ref Message msg)\n    {\n        if (msg.Msg == WM_HOTKEY && (int)msg.WParam == VK_TTALK)\n        {\n            DialNumber("1234567890");\n        }\n    }\n\n    private void DialNumber(string phoneNumber)\n    {\n        // Use the Phone API to dial the number\n        System.Diagnostics.Process.Start("tel:" + phoneNumber);\n    }\n}