// Assuming ServiceContext and OutputContext are your data contexts\nServiceContext serviceContext = new ServiceContext();\nOutputContext outputContext = new OutputContext();\n\n// Retrieve lists of services and outputs\nList<Service> svcs = serviceContext.Services.ToList();\nList<Output> outs = outputContext.Outputs.ToList();\n\n// Attach the existing outputs to the service context to avoid re-insertion\nforeach (Output output in outs)\n{\n    // Attach the output to the service context in an unmodified state\n    if (!serviceContext.Outputs.Contains(output))\n    {\n        serviceContext.Outputs.Attach(output);\n    }\n}\n\n// Add the outputs to the service\nsvcs[0].OutputCollection.AddRange(outs);\n\n// Save changes to the database\nserviceContext.SubmitChanges();