using System;\nusing System.DirectoryServices.AccountManagement;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string domain = "YOUR_DOMAIN"; // Replace with your domain\n        string username = "USERNAME"; // Replace with the username you want to check\n\n        try\n        {\n            // Create a context for the local machine\n            using (PrincipalContext context = new PrincipalContext(ContextType.Machine))\n            {\n                // Find the user\n                UserPrincipal user = UserPrincipal.FindByIdentity(context, IdentityType.SamAccountName, username);\n\n                if (user != null)\n                {\n                    // Find the Administrators group\n                    GroupPrincipal adminGroup = GroupPrincipal.FindByIdentity(context, "Administrators");\n\n                    if (adminGroup != null && user.IsMemberOf(adminGroup))\n                    {\n                        Console.WriteLine($"{username} is a member of the Administrators group.");\n                    }\n                    else\n                    {\n                        Console.WriteLine($"{username} is not a member of the Administrators group.");\n                    }\n                }\n                else\n                {\n                    Console.WriteLine($"User {username} not found.");\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}