public class DataService\n{\n    public object GetData(SecurityToken token)\n    {\n        if (IsValidToken(token))\n        {\n            // Provide access to the data\n            return FetchData();\n        }\n        throw new UnauthorizedAccessException();\n    }\n\n    private bool IsValidToken(SecurityToken token)\n    {\n        string currentMachineFingerprint = GetMachineFingerprintFromClient();\n        return token.Expiry > DateTime.UtcNow &&\n               token.MachineFingerprint == currentMachineFingerprint;\n    }\n\n    private string GetMachineFingerprintFromClient()\n    {\n        // Extract the machine fingerprint from the client request\n        return "client-machine-fingerprint";\n    }\n\n    private object FetchData()\n    {\n        // Implementation to fetch data\n        return new { Data = "Sample Data" };\n    }\n}