using System;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlData = @"\n        <sales>\n            <item name='Games' sku='MIC28306200' iCat='28' sTime='11/26/2008 8:41:12 AM' price='1.00' desc='Item Name' />\n            <item name='Games' sku='MIC28307100' iCat='28' sTime='11/26/2008 8:42:12 AM' price='1.00' desc='Item Name' />\n            <!-- More items -->\n        </sales>";\n\n        XDocument doc = XDocument.Parse(xmlData);\n\n        var sortedItems = doc.Root.Elements("item")\n            .OrderBy(item => DateTime.Parse(item.Attribute("sTime").Value))\n            .ToList();\n\n        foreach (var item in sortedItems)\n        {\n            Console.WriteLine($"Name: {item.Attribute("name").Value}, SKU: {item.Attribute("sku").Value}, sTime: {item.Attribute("sTime").Value}");\n        }\n    }\n}