try\n{\n    using (SqlConnection connectionA = new SqlConnection("Connection_String_A"))\n    using (SqlConnection connectionB = new SqlConnection("Connection_String_B"))\n    {\n        await connectionA.OpenAsync();\n        await connectionB.OpenAsync();\n\n        using (SqlCommand sqlCmdA = new SqlCommand("SQL_Statement_A", connectionA))\n        using (SqlCommand sqlCmdB = new SqlCommand("SQL_Statement_B", connectionB))\n        {\n            await sqlCmdA.ExecuteNonQueryAsync();\n            await sqlCmdB.ExecuteNonQueryAsync();\n        }\n    }\n}\ncatch (Exception ex)\n{\n    Console.WriteLine("An error occurred: " + ex.Message);\n}