using System;\nusing Microsoft.Web.Administration;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string virtualPath = "/storage1/test";\n        string physicalPath = GetPhysicalPath(virtualPath);\n        if (!string.IsNullOrEmpty(physicalPath))\n        {\n            Console.WriteLine("Physical path: " + physicalPath);\n            // Now you can delete the files from the physical path\n            DeleteInactiveFiles(physicalPath);\n        }\n        else\n        {\n            Console.WriteLine("Virtual path not found.");\n        }\n    }\n\n    static string GetPhysicalPath(string virtualPath)\n    {\n        using (ServerManager serverManager = new ServerManager())\n        {\n            var site = serverManager.Sites["YourSiteName"]; // Replace with your site name\n            foreach (var app in site.Applications)\n            {\n                foreach (var vDir in app.VirtualDirectories)\n                {\n                    if (vDir.Path.Equals(virtualPath, StringComparison.OrdinalIgnoreCase))\n                    {\n                        return vDir.PhysicalPath;\n                    }\n                }\n            }\n        }\n        return null;\n    }\n\n    static void DeleteInactiveFiles(string physicalPath)\n    {\n        // Logic to delete files from the physical path\n        // Ensure you have the necessary permissions to delete files\n        DirectoryInfo di = new DirectoryInfo(physicalPath);\n        foreach (FileInfo file in di.GetFiles())\n        {\n            // Check if the file is inactive (e.g., based on your database records)\n            // If inactive, delete the file\n            file.Delete();\n        }\n    }\n}