public class AuthService\n{\n    public SecurityToken Authenticate(string username, string password)\n    {\n        // Verify username and password\n        if (IsValidUser(username, password))\n        {\n            string machineFingerprint = GetMachineFingerprintFromClient();\n            return GenerateSecurityToken(username, machineFingerprint);\n        }\n        throw new UnauthorizedAccessException();\n    }\n\n    private string GetMachineFingerprintFromClient()\n    {\n        // Extract the machine fingerprint from the client request\n        return "client-machine-fingerprint";\n    }\n\n    private SecurityToken GenerateSecurityToken(string username, string machineFingerprint)\n    {\n        // Create and return a security token that includes the machine fingerprint\n        return new SecurityToken\n        {\n            Username = username,\n            MachineFingerprint = machineFingerprint,\n            Expiry = DateTime.UtcNow.AddHours(1)\n        };\n    }\n}\n\npublic class SecurityToken\n{\n    public string Username { get; set; }\n    public string MachineFingerprint { get; set; }\n    public DateTime Expiry { get; set; }\n}