using Aspose.Email;\nusing Aspose.Email.Storage.Pst;\nusing System.IO;\n\npublic void ReadMsgFromMemoryStream(MemoryStream memoryStream)\n{\n    // Load the .msg file from the MemoryStream\n    var msg = MapiMessage.FromStream(memoryStream);\n\n    // Access various parts of the email\n    string subject = msg.Subject;\n    string body = msg.Body;\n    var fromAddress = msg.SenderEmailAddress;\n\n    // Display the email details (for example purposes)\n    Console.WriteLine($"Subject: {subject}");\n    Console.WriteLine($"Body: {body}");\n    Console.WriteLine($"From: {fromAddress}");\n}