using System;\nusing System.IO.Pipes;\n\npublic class ChildProcess\n{\n    public static void Main()\n    {\n        using (NamedPipeClientStream pipeClient = new NamedPipeClientStream(".", "SecurePipe", PipeDirection.In))\n        {\n            pipeClient.Connect();\n            using (StreamReader reader = new StreamReader(pipeClient))\n            {\n                string receivedString = reader.ReadLine();\n                Console.WriteLine("Received Secure String: " + receivedString);\n            }\n        }\n    }\n}