using System;\nusing System.Net.Mail;\n\npublic partial class Emailer : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        SendEmail();\n    }\n\n    private void SendEmail()\n    {\n        try\n        {\n            MailMessage mail = new MailMessage();\n            SmtpClient smtpServer = new SmtpClient("your.smtp.server");\n\n            mail.From = new MailAddress("your_email@domain.com");\n            mail.To.Add("to_email@domain.com");\n            mail.Subject = "Test Mail";\n            mail.Body = "This is for testing SMTP mail from aspx page.";\n\n            smtpServer.Port = 25;\n            smtpServer.Credentials = new System.Net.NetworkCredential("username", "password");\n            smtpServer.EnableSsl = true;\n\n            smtpServer.Send(mail);\n            Response.Write("Mail Sent");\n        }\n        catch (Exception ex)\n        {\n            Response.Write("Exception caught in SendEmail(): {0}", ex.ToString());\n        }\n    }\n}