using MimeKit;\nusing System.IO;\n\npublic void ReadEmlFromMemoryStream(MemoryStream memoryStream)\n{\n    // Load the .eml file from the MemoryStream\n    var message = MimeMessage.Load(memoryStream);\n\n    // Access various parts of the email\n    string subject = message.Subject;\n    string body = message.TextBody;\n    var fromAddress = message.From.Mailboxes.FirstOrDefault()?.Address;\n\n    // Display the email details (for example purposes)\n    Console.WriteLine($"Subject: {subject}");\n    Console.WriteLine($"Body: {body}");\n    Console.WriteLine($"From: {fromAddress}");\n}