using System;\nusing Tweetinvi;\n\nclass Program\n{\n    static void Main()\n    {\n        // Replace these with your actual Twitter API credentials\n        string consumerKey = "your_consumer_key";\n        string consumerSecret = "your_consumer_secret";\n        string accessToken = "your_access_token";\n        string accessTokenSecret = "your_access_token_secret";\n\n        // Set up your credentials\n        Auth.SetUserCredentials(consumerKey, consumerSecret, accessToken, accessTokenSecret);\n\n        // Validate credentials\n        var user = User.GetAuthenticatedUser();\n        if (user != null)\n        {\n            Console.WriteLine("User authenticated successfully!");\n            Console.WriteLine($"Username: {user.ScreenName}");\n        }\n        else\n        {\n            Console.WriteLine("Authentication failed. Please check your credentials.");\n        }\n    }\n}