using System;\nusing System.IO;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        string[] urls = new string[]\n        {\n            "http://localhost:5000/page1.aspx",\n            "http://localhost:5000/page2.aspx",\n            // Add more URLs as needed\n        };\n\n        foreach (var url in urls)\n        {\n            await ExportPageToHtml(url);\n        }\n    }\n\n    static async Task ExportPageToHtml(string url)\n    {\n        using (HttpClient client = new HttpClient())\n        {\n            try\n            {\n                string html = await client.GetStringAsync(url);\n                string fileName = GetFileNameFromUrl(url);\n                File.WriteAllText(fileName, html);\n                Console.WriteLine($"Exported {url} to {fileName}");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error exporting {url}: {ex.Message}");\n            }\n        }\n    }\n\n    static string GetFileNameFromUrl(string url)\n    {\n        var uri = new Uri(url);\n        string path = uri.AbsolutePath.Replace("/", "_").TrimStart('_');\n        return string.IsNullOrEmpty(path) ? "index.html" : path + ".html";\n    }\n}