using System;\nusing System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define the output file path\n        string outputFilePath = "output.pdf";\n\n        // Create a Document object\n        Document document = new Document();\n\n        try\n        {\n            // Create a PdfWriter instance, specifying the output file path\n            PdfWriter.GetInstance(document, new FileStream(outputFilePath, FileMode.Create));\n\n            // Open the document for writing\n            document.Open();\n\n            // Add a paragraph with some text to the document\n            document.Add(new Paragraph("Hello, this is a sample PDF document created using iTextSharp."));\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n        finally\n        {\n            // Close the document\n            document.Close();\n        }\n\n        Console.WriteLine("PDF file created successfully at " + outputFilePath);\n    }\n}