DirectoryEntry group = groupResult.GetDirectoryEntry();\nList<string> allMembers = new List<string>();\nint rangeStep = 1000; // Adjust the range step as needed\nint startRange = 0;\n\nwhile (true)\n{\n    string range = $"member;range={startRange}-{startRange + rangeStep - 1}";\n    group.RefreshCache(new[] { range });\n    PropertyValueCollection groupMembers = group.Properties[range];\n\n    if (groupMembers.Count == 0)\n    {\n        break; // No more members to fetch\n    }\n\n    foreach (string member in groupMembers)\n    {\n        allMembers.Add(member);\n    }\n\n    startRange += rangeStep;\n}\n\n// Now 'allMembers' contains all the members of the group