using System;\nusing System.Diagnostics;\nusing System.IO;\n\npublic class PdfGenerator\n{\n    public static void ConvertAspxToPdf(string aspxUrl, string outputFilePath)\n    {\n        string wkhtmltopdfPath = @"C:\Program Files\wkhtmltopdf\bin\wkhtmltopdf.exe"; // Adjust path as necessary\n\n        var processStartInfo = new ProcessStartInfo\n        {\n            FileName = wkhtmltopdfPath,\n            Arguments = $"\"{aspxUrl}\" \"{outputFilePath}\"",\n            UseShellExecute = false,\n            RedirectStandardOutput = true,\n            RedirectStandardError = true,\n            CreateNoWindow = true\n        };\n\n        using (var process = new Process())\n        {\n            process.StartInfo = processStartInfo;\n            process.Start();\n\n            string output = process.StandardOutput.ReadToEnd();\n            string error = process.StandardError.ReadToEnd();\n\n            process.WaitForExit();\n\n            if (process.ExitCode != 0)\n            {\n                throw new Exception($"wkhtmltopdf failed with exit code {process.ExitCode}. Error: {error}");\n            }\n        }\n    }\n}\n\n// Usage\nclass Program\n{\n    static void Main()\n    {\n        string aspxUrl = "http://localhost:12345/Report.aspx"; // Your ASPX URL\n        string outputFilePath = @"C:\path\to\output.pdf";\n\n        try\n        {\n            PdfGenerator.ConvertAspxToPdf(aspxUrl, outputFilePath);\n            Console.WriteLine("PDF generated successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}