public class SimpleItem\n{\n    private Dictionary<string, string> _items = new Dictionary<string, string>();\n\n    public string this[string key]\n    {\n        get\n        {\n            if (_items.ContainsKey(key))\n            {\n                return _items[key];\n            }\n            return string.Empty;\n        }\n        set\n        {\n            _items[key] = value;\n        }\n    }\n}