using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new process\n        Process process = new Process();\n        \n        // Configure the process using the StartInfo property\n        process.StartInfo.FileName = "java.exe";\n        process.StartInfo.Arguments = "-Xmx1024m -cp ./../libs/*;l2jserver.jar net.sf.l2j.gameserver.GameServer";\n        process.StartInfo.RedirectStandardOutput = true;\n        process.StartInfo.RedirectStandardError = true;\n        process.StartInfo.UseShellExecute = false;\n        process.StartInfo.CreateNoWindow = true;\n\n        // Define the event handlers to capture the output\n        process.OutputDataReceived += (sender, e) => \n        {\n            if (!string.IsNullOrEmpty(e.Data))\n            {\n                // Output to your textbox or handle the output as needed\n                Console.WriteLine("Output: " + e.Data);\n            }\n        };\n\n        process.ErrorDataReceived += (sender, e) =>\n        {\n            if (!string.IsNullOrEmpty(e.Data))\n            {\n                // Output to your textbox or handle the error as needed\n                Console.WriteLine("Error: " + e.Data);\n            }\n        };\n\n        // Start the process\n        process.Start();\n\n        // Begin reading the output\n        process.BeginOutputReadLine();\n        process.BeginErrorReadLine();\n\n        // Wait for the process to exit\n        process.WaitForExit();\n    }\n}