// Assuming you have a MenuStrip named 'menuStrip1'\nMenuStrip menuStrip1 = new MenuStrip();\n\n// Add menu items to the menu strip for demonstration\nToolStripMenuItem myMenuOption1MenuItem = new ToolStripMenuItem("Option 1");\nToolStripMenuItem myMenuOption2MenuItem = new ToolStripMenuItem("Option 2");\nToolStripMenuItem myMenuOption3MenuItem = new ToolStripMenuItem("Option 3");\nToolStripMenuItem myMenuOption4MenuItem = new ToolStripMenuItem("Option 4");\n\nmenuStrip1.Items.Add(myMenuOption1MenuItem);\nmenuStrip1.Items.Add(myMenuOption2MenuItem);\nmenuStrip1.Items.Add(myMenuOption3MenuItem);\nmenuStrip1.Items.Add(myMenuOption4MenuItem);\n\n// Example user class with IsAdministrator property\npublic class User\n{\n    public bool IsAdministrator { get; set; }\n}\n\nUser myUser = new User { IsAdministrator = true };\n\n// Loop through the menu items and enable/disable based on user privileges\nforeach (ToolStripMenuItem menuItem in menuStrip1.Items)\n{\n    menuItem.Enabled = myUser.IsAdministrator;\n}