using System.Data.Linq;\nusing System.Linq;\n\npublic List<MyEntity> GetEntities(string baseTable, Dictionary<string, object> parameters)\n{\n    using (DataContext context = new DataContext("YourConnectionString"))\n    {\n        var query = context.GetTable<MyEntity>().AsQueryable();\n\n        foreach (var param in parameters)\n        {\n            if (param.Key == "SomeColumn")\n            {\n                query = query.Where(e => e.SomeColumn == (int)param.Value);\n            }\n            // Add other conditions based on parameters\n        }\n\n        return query.ToList();\n    }\n}